catalog_script_client.script:
function onSubmit() {
	g_form.clearMessages();
	scrollToTop();
	var usersList = getSelectedUsersList();
	if(usersList.length == 0) {
		g_form.addErrorMessage("You haven't selected any object.");
		g_form.submitted = false;
		return false;
	}
	if(!(selectedManager.length > 0)) {
		g_form.addErrorMessage('"Manager" is a mandatory field. You can\'t leave it empty.');
		g_form.submitted = false;
		return false;
	}

	var gURL = new GlideURL();
	gURL.setFromCurrent();
	var parent = gURL.getParam('parent');
	var sys_id = gURL.getParam('sys_id');
	var admpLocation = getAdmpLocation(parent, sys_id); //get location, returns "module", "menu" or "catalog"
	if(admpLocation == "module" || admpLocation == "menu") {
		modifyManager();
	} else if(admpLocation == "catalog") {
		createIncident();
	}

	function modifyManager() {
		freezeWindow(true, 'Connecting ADManager Plus...');
		var ga = new GlideAjax("ADManagerAPIUtil");
		ga.addParam("sysparm_name", "modifyManager");
		ga.addParam("domainName", g_form.getValue('Select_Domain'));
		ga.addParam("inputFormat", constructInputFormat());
		ga.addParam("buildNumber", getBuildNumber());
		ga.addParam("encryptedAuthToken", getEncryptedAuthToken());
		ga.getXMLAnswer(modifyManagerCallback);
		function modifyManagerCallback(response) {
			freezeWindow(false);
			response = JSON.parse(response);
			if(response.status == 'true') {
				var responseBody = JSON.parse(response.responseBody);
				var statusArray = [];
				for(var i = 0; i < responseBody.length; i++) {
					var status = {};
					status.LOGON_NAME = responseBody[i].userPrincipalName;
					status.sAMAccountName = responseBody[i].sAMAccountName;
					status.statusMessage = responseBody[i].statusMessage;
					statusArray.push(status);

					var sys_id = gURL.getParam('sys_id');
					if(sys_id != null && sys_id != '') {
						var successMsg = responseBody[i].statusMessage;
						if(responseBody[i].status == '1') {
							successMsg += '\n Full Name : ' + responseBody[i].sAMAccountName + '\n Manager Name: ' + getManagerName() + '\n Manager Distinguished Name: ' + getManagerDN();
						}
						updateComments(sys_id, successMsg);
					}
				}
				updateStatus(statusArray);
			} else {
				if(response.statusMessage) g_form.addErrorMessage(response.statusMessage);
				else g_form.addErrorMessage(response.responseBody);
			}
		}
	}

	function constructInputFormat() {
		var inputFormat = [];
		var userDetails = usersList[0];
		userDetails.manager = getManagerDN();
		inputFormat.push(userDetails);
		return JSON.stringify(inputFormat);
	}

	function updateComments(sys_id, commment) {
		var ga = new GlideAjax("ADManagerIncidentHandler");
		ga.addParam("sysparm_name", "addComments");
		ga.addParam("comments", commment);
		ga.addParam("sys_id", sys_id);
		ga.getXMLAnswer(addCommentsCallback);
		function addCommentsCallback(response) {
		}
	}

	function createIncident() {
		var requestDetails = constructRequestDetails();
		var comments = constructComments(requestDetails);
		freezeWindow(true, 'Loading, please wait...');
		var ga = new GlideAjax('ADManagerIncidentHandler');
		ga.addParam("sysparm_name", "createIncident");
		ga.addParam("requestDetails", requestDetails);
		ga.addParam("description", "Modify the Manager attribute of the following user.");
		ga.addParam("comments", comments);
		ga.getXMLAnswer(createIncidentCallback);
		function createIncidentCallback(response) {
			freezeWindow(false);
			g_form.addInfoMessage('Your request has been successfully submitted to the IT team.');
			emptyAllFields();
		}
	}

	function constructRequestDetails() {
		var requestDetails = {};
		requestDetails.domainName = g_form.getValue('Select_Domain');
		requestDetails.managerName = getManagerName();
		requestDetails.managerDN = getManagerDN();
		requestDetails.searchText = getSearchText();
		requestDetails.usersList = getSelectedUsersList();
		requestDetails = JSON.stringify(requestDetails);
		return requestDetails;
	}

	function constructComments(requestDetails) {
		requestDetails = JSON.parse(requestDetails);
		var comments = '[code]<ul class="sn-widget-list sn-widget-list-table">';
		comments += '<li class="ng-scope"><span class="sn-widget-list-table-cell">Selected Domain: </span><span class="sn-widget-list-table-cell"><span class="ng-scope">'+requestDetails.domainName+'</span></span></li>';
		comments += '<li class="ng-scope"><span class="sn-widget-list-table-cell">Selected Manager Name: </span><span class="sn-widget-list-table-cell"><span class="ng-scope">'+requestDetails.managerName+'</span></span></li>';
		comments += '<li class="ng-scope"><span class="sn-widget-list-table-cell">Selected Manager Distinguished Name: </span><span class="sn-widget-list-table-cell"><span class="ng-scope">'+requestDetails.managerDN+'</span></span></li>';
		comments += '<li class="ng-scope"><span class="sn-widget-list-table-cell">Selected User: </span><span class="sn-widget-list-table-cell"><span class="ng-scope">';
		var userList = requestDetails.usersList;
		for(var i = 0; i < userList.length; i++) {
			var userName = userList[i].DISPLAY_NAME;
			if(userName == "-") {
				userName = userList[i].userPrincipalName;
			}
			if(i == 0) comments = comments + userName;
			else if(i == userList.length - 1) comments = comments + '  &  ' + userName + '.';
			else comments = comments + ',  ' + userName;
		}
		comments += '</span></span></li></ul>[/code]';
		return comments;
	}

	g_form.submitted = false;
	return false;
}
