sys_script_include.script:
/*
 * ADManager MID Server Handler
 */
var ADManagerMIDServerHandler = Class.create();
ADManagerMIDServerHandler.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	// This will update Use MID Server field in all ADManager REST Messages with user provided MID Server;

	setMIDServerToRestMessages: function(sys_id) {
		var restMessageRecord = new GlideRecord('sys_rest_message_fn');
		restMessageRecord.addQuery('rest_endpoint', 'IN', '${url}/ADMPAPI/AddRemoveGroups, ${url}/RestAPI/ADGroupList, ${url}/RestAPI/SearchUser, ${url}/RestAPI/CreateBulkUser, ${url}/ADMPAPI/DeleteUser, ${url}/ADMPAPI/DisableUser, ${url}/ADMPAPI/EnableUser, ${url}/RestAPI/FSMFolderList, ${url}/RestAPI/GetAllTechnicianDetails, ${url}/RestAPI/GetAuthInfo, ${url}/RestAPI/APIAuthToken, ${url}/MobileAPI/MobileLogin, ${url}/MobileAPI/getManagerValues, ${url}/RestAPI/GroupsWithPermission, ${url}/servlet/GetProductVersion, ${url}/RestAPI/RemoveFolderPermissions, ${url}/ADMPAPI/ResetPwd, ${url}/RestAPI/SetFolderPermissions, ${url}/MobileAPI/MTemplateNames, ${url}/ADMPAPI/UnlockUser, ${url}/RestAPI/ValidateAuthToken, ${url}/RestAPI/GetPublicKey, ${url}/RestAPI/GetDepartmentList, ${url}/RestAPI/GetOrganizationAttributes, ${url}/RestAPI/ModifyUser, ${url}/RestAPI/ModifyManager, ${url}/RestAPI/ModifyDepartment, ${url}/RestAPI/GetUserCreationFormFields');
		restMessageRecord.query();
		while(restMessageRecord.next()) {
			restMessageRecord.use_mid_server = sys_id;
			restMessageRecord.update();
		}
	},
    type: 'ADManagerMIDServerHandler'
});
