sys_ui_page.client_script:
var range= 20;
var selectedManager = [];
var selectedManager_Temp = [];

function onLoadFunction() {
	getADManagerList(1);
	selectedManager = GlideDialogWindow.get().getPreference('selectedManager');
	selectedManager_Temp = selectedManager.concat();	
}

var oldManagerSearchText = '';

function getADManagerList(startIndex, isNav) {
	if(startIndex == 0) startIndex = $j('#manager_start_index').html(); 
	if(startIndex == 0) startIndex = 1;
	$j('#loadingIcon').show();
	$j('#ADManagerListTable').hide();
	$j('#ADManagerListTableHeader').hide();
	$j('#errorMsg').hide();

	var selectedDomain = GlideDialogWindow.get().getPreference('selectedDomain');
	$j('#domain_select').html('<option selected>'+selectedDomain+'</option>');

	var searchText = $j('#searchText').val();
	searchText = searchText.replace(/\\/g, '\\\\');
// 	var managerListAjax = new GlideAjax("ADManagerManagersListHandler");
// 	managerListAjax.addParam("sysparm_name","getManagersList");
// 	managerListAjax.addParam("searchText",searchText);
// 	managerListAjax.addParam("domainName",selectedDomain);
// 	managerListAjax.addParam("startIndex",startIndex.toString());
// 	managerListAjax.addParam("range",range.toString());
// 	managerListAjax.getXMLAnswer(managerListResponse);

	if(isNav) searchText = oldManagerSearchText;

	var gURL = new GlideURL();
	gURL.setFromCurrent();
	var parent = gURL.getParam('parent');

	var managerListAjax = new GlideAjax("ADManagerUserListHandler");
	managerListAjax.addParam("sysparm_name", "getADUsersList");
	managerListAjax.addParam("searchText", searchText);
	managerListAjax.addParam("domainName", selectedDomain);
	managerListAjax.addParam("startIndex", startIndex.toString());
	managerListAjax.addParam("range", range.toString());
	managerListAjax.addParam("buildNumber", getBuildNumber());
	managerListAjax.addParam("encryptedAuthToken", getEncryptedAuthToken());
	if(!(parent && (parent == 'module' || parent == 'menu') && g_user.hasRole('x_manen_admanager.admanager_admin')) && g_user.hasRole('x_manen_admanager.admanager_requester')) {
			managerListAjax.addParam("is_requester", "true");
		}
	managerListAjax.getXMLAnswer(managerListResponse);

	function managerListResponse(managerList)  {
		var result = managerList.evalJSON(); 
		if(result.status == 'true') {
			loadManagerList(managerList, false);
			oldManagerSearchText = searchText;
		}
		else {
			$j('#loadingIcon').hide();
			$j('#errorMsg').show();
			$j('#errorMsg').html("<h5 style=\"text-align: center;margin-top:50px;\">"+result.responseBody+"</h5>");
		}
	}
}

function loadManagerList(managerListJSON) {
	managerListJSON = managerListJSON.evalJSON(); 
	$j('#manager_start_index').html(managerListJSON.startIndex);
	$j('#manager_end_index').html(managerListJSON.endIndex);
	$j('#manager_total_count').html(managerListJSON.count);
	// 	var managersList = managerListJSON.ManagersList;
	var managersList = managerListJSON.UsersList;

	var html = '';
	if(managersList.length > 0) {
		for(var i = 0; i < managersList.length; i++) {
			var managerName = managersList[i].DISPLAY_NAME? managersList[i].DISPLAY_NAME: '-';

			var distinguishedName = managersList[i].DISTINGUISHED_NAME? managersList[i].DISTINGUISHED_NAME: '-';
			if(distinguishedName == '-') distinguishedName = managersList[i].distinguishedName? managersList[i].distinguishedName: '-';

			var location = managersList[i].OU_NAME? managersList[i].OU_NAME: '-';
// 			var location = managersList[i].LOCATION? managersList[i].LOCATION: '-';
			var objectClass = managersList[i].OBJECT_CLASS? managersList[i].OBJECT_CLASS: 'User';
			var isChecked = isSelected(distinguishedName)?'checked' : '';

			if(i % 2 == 1) html += '<tr class="list_row list_odd">';
			else html += '<tr class="list_row list_even">';

				html += '<td id="radioButton" style="max-width:40px" class="list_decoration_cell col-control col-small col-center" rowspan="1" scope="row"><span class="input-group-radio"><input data-name = "'+escapeHtml(managerName)+'" data-distinguishedname = "'+escapeHtml(distinguishedName)+'" name="selectManager" id="selectManager_'+escapeHtml(distinguishedName)+'" data-type="list2_radio" type="radio" class="radio"  value = "'+escapeHtml(distinguishedName)+'" '+isChecked+'></input><label for="selectManager_'+escapeHtml(distinguishedName)+'" class="radio-label"></label></span></td>';

			html += '<td class="vt vt-spacer overflow-ellipsis" id="managerName" style="max-width:200px" title="'+ escapeHtml(managerName) +'">' + escapeHtml(managerName) + '</td>';
			html += '<td class="vt vt-spacer overflow-ellipsis" id="location" style="max-width:450px" title="'+ escapeHtml(location) +'">' + escapeHtml(location) + '</td>';
			html += '<td class="vt vt-spacer overflow-ellipsis" id="objectClass" style="max-width:100px" title="'+ escapeHtml(objectClass) +'">' + escapeHtml(objectClass) + '</td>';
			html += '</tr>';
		}
	}
	else {
		$j('#manager_start_index').html(0);
		html += '<tr class="list2_no_records"><td colspan="4">No records to display</td></tr>';
	}

	$j("#ADManagerListBody").html(html);
	$j('#loadingIcon').hide();
	$j('#ADManagerListTable').show();
	$j('#ADManagerListTableHeader').show();
	$j('#radioHeader').width($j('#radioButton').width());
	$j('#managerNameHeader').width($j('#managerName').width());
	$j('#locationHeader').width($j('#location').width());
	$j('#objectClassHeader').width($j('#objectClass').width());

	$j("#radioHeader").html('');
	$j('[name=selectManager]').change(function(){  //"select all" change 
		var managerDetails = {};
		managerDetails.name = $j(this).data('name');
		managerDetails.distinguishedname = $j(this).data('distinguishedname');
		selectedManager_Temp.splice(0, selectedManager_Temp.length);
		selectedManager_Temp.push(managerDetails);

	});
}

function escapeHtml(str) {
	return String(str).replace(/[\u00A0-\u9999<>\&]/gim, function(i) {
		return '&#'+i.charCodeAt(0)+';';
	});
}

function navigateFirstManagerList() {
	var StartIndex = parseInt($j('#manager_start_index').html());
	if(StartIndex>20)
		getADManagerList(1, true);
}

function navigateBackManagerList() {
	var total_count = parseInt($j('#manager_total_count').html());
	var StartIndex = parseInt($j('#manager_start_index').html());

	if(StartIndex-20>=1)
		getADManagerList(StartIndex-20, true);
}

function navigateNextManagerList() {
	var total_count = parseInt($j('#manager_total_count').html());
	var StartIndex = parseInt($j('#manager_start_index').html());

	if(StartIndex+20<=total_count) getADManagerList(StartIndex+20, true);
}

function navigateLastManagerList() {
	var total_count = parseInt($j('#manager_total_count').html());
	var StartIndex = parseInt($j('#manager_start_index').html());

	if(StartIndex+20<=total_count) {
		var startIndex = 1;
		if(total_count%20 ==0) startIndex = total_count-19;
		else startIndex = total_count-(total_count%20)+1;
		getADManagerList(startIndex, true);
	}
}

function KeyPressEvent(e) {
	if(e.keyCode == 13) {
		searchManager();
	}
}

function refreshManagerList() {
	$j('#searchText').val('');
	getADManagerList(1);
}

function searchManager() {
// 	if($j('#searchText').val() == '')
// 		alert("Please enter text to search.");
// 	else
		getADManagerList(1);
}

function updateManager() {
	selectedManager.splice(0, selectedManager.length);
	for(var i=0; i<selectedManager_Temp.length; i++) {
		selectedManager.push(selectedManager_Temp[i]);
	}
	updateForm();
	GlideDialogWindow.get().destroy();
}

function isSelected(distinguishedName) {
	for(var i=0; i<selectedManager_Temp.length; i++) {
		if(distinguishedName == selectedManager_Temp[i].distinguishedname) return true;
	}
	return false;
}
